import React from "react";

const MediaMagicalSection = () => {
  return (
    <div className="bg-black text-white min-h-screen p-8">
      {/* Header Section */}
      <div className="text-center mb-12">
        <h1 className="text-5xl font-bold leading-tight">
          Make.
          <br />
          Media.
          <br />
          Magical.
        </h1>
        <button className="mt-6 px-6 py-3 bg-white text-black font-medium rounded-full hover:shadow-lg">
          Join Waitlist
        </button>
      </div>

      {/* Main Content Section */}
      <div className="flex flex-col lg:flex-row gap-8">
        {/* Image Preview Section */}
        <div className="bg-gray-900 rounded-xl p-4 relative">
          <img
            src="/room-preview.jpg" // Replace with your image path
            alt="Room Preview"
            className="rounded-lg w-full"
          />
          {/* Action Buttons */}
          <div className="absolute top-4 right-4 flex flex-col gap-2">
            <button className="bg-black p-2 rounded-lg text-white hover:bg-gray-700">
              üì• {/* Replace with an icon or SVG */}
            </button>
            <button className="bg-black p-2 rounded-lg text-white hover:bg-gray-700">
              üóëÔ∏è {/* Replace with an icon or SVG */}
            </button>
          </div>
        </div>

        {/* Asset and Settings Section */}
        <div className="flex flex-col gap-6 w-full">
          {/* Asset List */}
          <div className="bg-gray-900 rounded-xl p-4">
            <h3 className="font-semibold text-lg mb-4">Assets</h3>
            <ul className="space-y-3">
              {[
                "Sofa",
                "Wooden Shelf",
                "Sofa Single Seater",
                "Sofa Single Seater",
                "Coffee Table",
              ].map((asset, index) => (
                <li
                  key={index}
                  className="flex items-center justify-between bg-black p-3 rounded-lg"
                >
                  <span>{asset}</span>
                  <button className="text-red-500 hover:text-red-400">
                    üóëÔ∏è
                  </button>
                </li>
              ))}
            </ul>
            <button className="mt-4 w-full bg-gray-800 p-3 rounded-lg text-white hover:bg-gray-700">
              + Add more images
            </button>
          </div>

          {/* Settings */}
          <div className="bg-gray-900 rounded-xl p-4">
            {/* Upscale Section */}
            <div className="mb-6">
              <h3 className="font-semibold text-lg mb-4">Upscale</h3>
              <div className="flex gap-4">
                {["2x", "4x", "8x", "16x"].map((scale, index) => (
                  <button
                    key={index}
                    className={`p-3 rounded-lg ${
                      scale === "8x"
                        ? "bg-white text-black"
                        : "bg-gray-800 text-white"
                    } hover:bg-gray-700`}
                  >
                    {scale}
                  </button>
                ))}
              </div>
            </div>

            {/* Style Section */}
            <div>
              <h3 className="font-semibold text-lg mb-4">Style</h3>
              <select className="w-full bg-gray-800 p-3 rounded-lg text-white focus:outline-none">
                <option>Photo-realistic</option>
                <option>Cartoon</option>
                <option>3D Render</option>
              </select>
            </div>
          </div>
        </div>
      </div>
    </div>
    
  );
};

export default MediaMagicalSection;
